Ext.define('BurgerQueen.view.TrayWindow', {
    extend: 'Ext.window.Window',

    requires: [
        'Ext.grid.Panel',
        'Ext.grid.View',
        'Ext.grid.plugin.CellEditing',
        'Ext.form.field.Text',
        'Ext.grid.column.Number',
        'Ext.toolbar.Toolbar',
        'Ext.button.Button',
        'Ext.form.field.Display'
    ],

    height: 399,
    id: 'TrayWindow',
    itemId: 'TrayWindow',
    width: 619,
    header: false,
    title: '',
    modal: true,

    layout: {
        type: 'vbox',
        align: 'stretch'
    },

    initComponent: function() {
        var me = this;

        Ext.applyIf(me, {
            items: [
                {
                    xtype: 'gridpanel',
                    id: 'trayGrid',
                    itemId: 'trayGrid',
                    header: false,
                    title: 'My Grid Panel',
                    store: 'TrayStore',
                    viewConfig: {
                        id: 'trayGridView',
                        itemId: 'trayGrid'
                    },
                    columns: [
                        {
                            xtype: 'gridcolumn',
                            id: 'orderName',
                            itemId: 'orderName',
                            dataIndex: 'Name',
                            text: 'Name',
                            flex: 0.8
                        },
                        {
                            xtype: 'gridcolumn',
                            id: 'orderQty',
                            itemId: 'orderQty',
                            dataIndex: 'Quantity',
                            text: 'Quantity',
                            flex: 0.5,
                            editor: {
                                xtype: 'textfield',
                                id: 'orderQtyField',
                                itemId: 'orderQtyField',
                                inputId: 'orderQtyId'
                            }
                        },
                        {
                            xtype: 'numbercolumn',
                            id: 'orderPrice',
                            itemId: 'orderPrice',
                            dataIndex: 'Price',
                            text: 'Price',
                            flex: 0.5
                        },
                        {
                            xtype: 'gridcolumn',
                            renderer: function(value, metaData, record, rowIndex, colIndex, store, view) {
                                var price = record.data.Price,
                                    quantity = record.data.Quantity,
                                    trayStore = Ext.getStore('TrayStore'),
                                    idEdit = record.data.Id;
                                record = trayStore.getById(idEdit);
                                var newTotal = price * quantity;
                                record.set('Total', newTotal);
                                return newTotal;

                            },
                            id: 'total',
                            itemId: 'total',
                            dataIndex: 'Total',
                            text: 'Total'
                        }
                    ],
                    plugins: [
                        Ext.create('Ext.grid.plugin.CellEditing', {
                            pluginId: 'cellEdit',
                            listeners: {
                                edit: {
                                    fn: me.onCellEditingEdit,
                                    scope: me
                                }
                            }
                        })
                    ]
                },
                {
                    xtype: 'toolbar',
                    layout: {
                        type: 'hbox',
                        pack: 'center'
                    },
                    items: [
                        {
                            xtype: 'button',
                            id: 'removeItemBtn',
                            itemId: 'removeItemBtn',
                            text: 'Remove Item'
                        }
                    ]
                },
                {
                    xtype: 'displayfield',
                    id: 'totalItems',
                    itemId: 'totalItems',
                    width: 150,
                    fieldLabel: 'Total Number of Items in Tray',
                    value: ''
                },
                {
                    xtype: 'displayfield',
                    flex: 1,
                    id: 'totalAmount',
                    itemId: 'totalAmount',
                    fieldLabel: 'Total Amount in RMB',
                    value: ''
                }
            ],
            dockedItems: [
                {
                    xtype: 'toolbar',
                    flex: 1,
                    dock: 'bottom',
                    layout: {
                        type: 'hbox',
                        pack: 'center'
                    },
                    items: [
                        {
                            xtype: 'button',
                            id: 'checkoutBtn',
                            itemId: 'checkoutBtn',
                            text: 'CheckOut'
                        },
                        {
                            xtype: 'button',
                            id: 'cancelCheckoutBtn',
                            itemId: 'cancelCheckoutBtn',
                            text: 'Cancel'
                        }
                    ]
                }
            ]
        });

        me.callParent(arguments);
    },

    onCellEditingEdit: function(editor, e, eOpts) {
        var grid = Ext.getCmp('trayGrid'),
            store = grid.getStore(),
            totalQuantity = 0,
            totalAmount = 0,
            records = store.getRange();

        Ext.each(records,function(record){
            totalQuantity += record.data.Quantity;
            totalAmount += record.data.Total;
        });

        console.log(totalQuantity, totalAmount);

        Ext.getCmp('totalItems').setValue(totalQuantity);
        Ext.getCmp('totalAmount').setValue(totalAmount);
        console.log('test');
    }

});